targets.expr:
(
  sum by(country_code) (
    last_over_time(
      icmp_ping_packets_received{
        continent=~"$continent",
        country_code=~"$country",
        city=~"$city",
        isp=~"$isp",
        endpoint=~"$endpoint"
      }[$__range]
    )
  )
  /
  sum by(country_code) (
    last_over_time(
      icmp_ping_packets_sent{
        continent=~"$continent",
        country_code=~"$country",
        city=~"$city",
        isp=~"$isp",
        endpoint=~"$endpoint"
      }[$__range]
    )
  )
)

